mapper.update:
UPDATE s_user t
SET t.logintimes    =t.logintimes+ 1,
t.lastlogintime = sysdate,
t.lastloginip   = #{lastloginip}
WHERE t.userid = #{userid}

mapper.insert:
SELECT sys_guid() from dual
insert into s_user( userid , username , password , usertype , enabled , registtime , fromaddr , ismainaccount, ISBLACKLIST, baseinfoid, email, mobile, name
) values ( #{userid} , #{username} , #{username} , #{usertype} , '1' , sysdate , 'sxjyggfw' , '1', '0', #{baseinfoid}, #{email}, #{mobile}, #{name} )
